import React, { useState } from 'react';
import { useChat } from '@nlxai/chat-react';
import { chatConfig } from '../../config/chatConfig';
import './styles.modules.css';  // Note: Remove .css from import if using CSS modules

const ChatBot = () => {
  const [inputMessage, setInputMessage] = useState('');
  const { messages, sendMessage } = useChat(chatConfig);  // Changed from chat.messages to messages

  const handleSendMessage = () => {
    if (inputMessage.trim()) {
      sendMessage(inputMessage);
      setInputMessage('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  return (
    <div className="chatContainer">
      <h2>Chatbot</h2>
      <ul className="messageList">
        {messages && messages.map((message, index) => (  // Added null check
          <li key={index} className={`message ${message.type}`}>
            {message.text}
          </li>
        ))}
      </ul>
      <div className="inputContainer">
        <input
          className="messageInput"
          type="text"
          value={inputMessage}
          onChange={(e) => setInputMessage(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type a message..."
        />
        <button className="sendButton" onClick={handleSendMessage}>
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatBot;
